function shuffle(array) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }
  return array;
}
const shuffleLogos = () => {
  const shuffled = [...shuffledLogos];
  for (let i = shuffled.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
  }
  setShuffledLogos(shuffled);
};
export const LogoCloud = () => {
  const [shuffledLogos, setShuffledLogos] = useState([...logos]);

  // Create a function to shuffle logos with animation
  const shuffleLogosWithAnimation = () => {
    const shuffled = [...shuffledLogos];

    // Create an animation sequence for shuffling
    const animations = shuffled.map((_, index) => {
      return {
        opacity: 0, // Start with opacity 0
        y: -10, // Start with a small upward translation
        transition: {
          duration: 0.3,
          delay: index * 0.1, // Add a delay based on the index for staggered animation
        },
      };
    });

    // Shuffle the logos
    for (let i = shuffled.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
    }

    // Update the shuffled logos with animation
    setShuffledLogos(shuffled);

    // Animate the shuffled logos into view
    setAnimations(animations);
  };

  // Call the shuffle function with animation on a timer
  useEffect(() => {
    const shuffleTimer = setInterval(() => {
      shuffleLogosWithAnimation();
    }, 3000); // Shuffle every 3 seconds

    return () => clearInterval(shuffleTimer); // Cleanup the timer on unmount
  }, []);

  // Define animations state to control the motion animations
  const [animations, setAnimations] = useState([]);

  return (
    <section className="max-w-screen-xl px-4 pt-12 pb-10 mx-auto md:pb-12 md:pt-16 sm:px-6 lg:px-8">
      {/* Section text */}
      <div className="w-full max-w-xl mx-auto text-center lg:max-w-3xl md:max-w-2xl">
        <p className="inline-flex items-center justify-center px-6 py-2 text-sm font-medium tracking-wide text-white rounded-r-full rounded-tl-full bg-gradient-to-r from-dark-600 to-dark-700">
          {/* Our partners */}
        </p>
        <h2 className="mt-6 text-3xl font-extrabold text-white sm:text-4xl md:text-5xl">
          Industry We Serve
        </h2>
        <p className="mt-6 text-xl text-dark-300">
          Some of the industries we serve with our services and solutions
        </p>
      </div>

      {/* Logos */}
      <div className="max-w-screen-lg mx-auto mt-12 lg:mt-14">
        <div className="grid grid-cols-2 gap-4 md:gap-6 sm:grid-cols-3">
          {shuffle([...logos])
            .slice(0, 6)
            .map((item, index) => (
              <Link href={item.href} key={`cloud-logo-${index}`}>
                <div className="overflow-hidden">
                  <motion.div
                    className="flex flex-col items-center justify-center w-full h-36 rounded-3xl hover:bg-dark-800 shadow-inner-clay move-right"
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.3, delay: index * 0.1 }}
                  >
                    <item.logo className="group-hover:text-white text-dark-400 mb-2" />
                    <span className="text-gray-300">{item.name}</span>
                  </motion.div>
                </div>
              </Link>
            ))}
        </div>
      </div>
    </section>
  );
};
